import {useState, useEffect} from 'react';
import {useLocation} from 'react-router-dom';

import axios from 'axios';
import './Create.css'

const Edit = () => {

    // CALL TO THE INFO OF THE SUPERHERO
    const location = useLocation(); 
    const {id} = location.state;

    const [superhero, setSuperhero] = useState([""]);

    const [superId, setSuperId] = useState(parseInt(id));


    useEffect(()=> {  
        const getSuperheroData = async () => {
            let resSuper = await axios.get(`http://localhost:3007/superheroes/${superId}`);
            setSuperhero(resSuper.data);
            
         }
        getSuperheroData();
        
    }, [superId]);

    const [name, setName] = useState(superhero.name);
    const [publisher, setPublisher] = useState(superhero.publisher);
    const [alter_ego, setAlterEgo] = useState(superhero.alter_ego);
    const [first_appearance, setFirstAppearance] = useState(superhero.first_appearance);
    const [image, setImage] = useState(superhero.image);
    const [characters, setCharacters] = useState(superhero.characters);

  const url = `http://localhost:3007/superheroes/${superId}`;

  console.log(superhero.name);

  const [info, setInfo] = useState({
    name: superhero.name, 
    publisher: superhero.publisher , 
    alter_ego: superhero.alter_ego ,
    first_appearance: superhero.first_appearance ,
    image:  superhero.image,
    characters: superhero.characters
  })

  function submit(e){
    e.preventDefault();
    axios.patch(url, {
      name: info.name,
      publisher:info.publisher, 
      alter_ego:info.alter_ego,
      first_appearance:info.first_appearance,
      image: info.image,
      characters:info.characters,
    })
    .then(res=>{
      console.log(res.info);
    })
  }

  function handle(e){
    const newData = {...info};
    newData[e.target.id] = e.target.value;
    setInfo(newData);
    console.log(newData);
  }

     
    return (
      <div className='create'> 
      

      <form className='creation-form' onSubmit={(e)=> submit(e)}> 

      <h1>Now editing: {superhero.name}</h1>

      <label for="fname">Name</label>
      <input type="text" name="name" placeholder={superhero.name}  
      onChange={(e)=>{setName(e.target.value)}} id="name" value={info.name}/>

      <label for="falter-ego">Alter Ego</label>
      <input type="text" name="alter_ego" placeholder={superhero.alter_ego} 
      onChange={(e)=>{setAlterEgo(e.target.value)}} id="alter_ego" value={info.alter_ego}/>

      <label for="fcharacters">Other Characters</label>
      <input type="text" name="characters" placeholder={superhero.characters} 
      onChange={(e)=>{setCharacters(e.target.value)}} id="characters" value={info.characters}/>

      <label for="ffirstappearance">First Appearance</label>
      <input type="text" name="first_appearance" placeholder={superhero.first_appearance} 
      onChange={(e)=>{setFirstAppearance(e.target.value)}} id="first_appearance" value={info.first_appearance}/>

      <label for="fpublisher">Publisher</label>
      <input type="text" name="publisher" placeholder={superhero.publisher} 
      onChange={(e)=>{setPublisher(e.target.value)}} id="publisher" value={info.publisher}/>

      <label for="fimage">Image URL</label>
      <input type="text" name="image" placeholder={superhero.image} 
      onChange={(e)=>{setImage(e.target.value)}} id="image" value={info.image}/>
             

      <button type='submit' className="btn" >Send</button>

      </form>
      </div>
    )
  }
  
  export default Edit;
