import {useState, useEffect} from 'react';
import axios from 'axios';
import './Create.css'

const Create = () => {

  const [superhero, setSuperhero] = useState([""]);

  useEffect(() => {
    const article = { title: 'React Hooks POST Request Example' };
    axios.post('https://reqres.in/api/articles', article)
        .then(response => setSuperhero(response.data.id));
    
    setSuperhero();

    }, []);
     
    return (
      <div className='create'> 
      <form className='creation-form'> 
      <label for="fname">Name</label>
      <input type="text" id="fname" name="name" placeholder="Superhero's name" required/>

      <label for="fname">Alter Ego</label>
      <input type="text" id="fname" name="name" placeholder="Your superhero's real identity" required/>

      <label for="fname">Other Characters</label>
      <input type="text" id="fname" name="name" placeholder="Who else has taken their mantle?" required/>

      <label for="fname">First Appearance</label>
      <input type="text" id="fname" name="name" placeholder="A comic, probably" required/>

      <label for="fname">Publisher</label>
      <input type="text" id="fname" name="name" placeholder="Publishing Company" required/>

      <label for="fname">Img URL</label>
      <input type="text" id="fname" name="name" placeholder="Add here your url" required/>

      
      
             

         <button type='submit' className="btn" onClick={setSuperhero} >Create</button>
      </form>
      </div>
    )
  }
  
  export default Create;
