import './Sh_card.css'
import { Link } from 'react-router-dom';
import { BiTrash } from 'react-icons/bi'
import axios from 'axios';


export default function Superhero_card(props) {
    
    const id = props.id;
    const deleteSuperhero = (id) => {
        console.log(id);
        axios.delete(`http://localhost:3007/superheroes/${id}`);
        
    }

    return (
         
        <div className="container">
            <div className="border">
                <div className="superhero-card">
                    <div className="text">
                         <h4 className="title">{props.name}</h4>
                            <img className="img" src={props.image}/>
                 
                    </div>
                    <div className="buttons">
                        <Link className='link' to="/Superhero" state={{id: `${props.id}`}}>
                            <button className="btn">Details</button>
                        </Link>
                        <Link className='link' to="/Superhero" state={{id: `${props.id}`}}>
                            <button className="btn">Edit</button>
                        </Link>
                        <button className="btn" onClick={deleteSuperhero}><BiTrash/></button>
                     </div>
                </div>
            </div>
        </div>     
    );
}
